mainCount :: [Char] -> [(Char, Int)]
mainCount x = freq x []

freq :: [Char] -> [(Char, Int)] -> [(Char, Int)]
freq "" y = y
freq (x:xs) y = freq xs (addInfo x y)


sortByFreq :: [Char] -> [(Char, Int)]
sortByFreq x = qsort (mainCount x)

mapping :: [Char] -> [(Char, Char)]
mapping x = zipIt (sortByFreq x) freqData

replace1 :: Char -> [(Char, Char)] -> Char
replace1 x [] = x
replace1 x (y:ys) = if (fst y)==x then (snd y) else replace1 x ys

subDecipher1 :: [Char] -> [(Char, Char)] -> [Char]
subDecipher1 [] y = []
subDecipher1 (x:xs) y = (replace1 x y) : subDecipher1 xs y

temp :: [(Char,Char)]
temp = [(' ',' '),('e','e'),('t','t'),('a','a'),('o','o'),('i','i'),('n','n'),('s','s'),('r','r'),('h','h'),('d','d'),('l','l'),('u','u'),('c','c'),('m','m'),('f','f'),('y','y'),('w','w'),('g','g'),('p','p'),('b','b'),('v','v'),('k','k'),('x','x'),('q','q'),('j','j'),('z','z')]

