replace21 :: [Char] -> [Char]
replace21 x = parser1 [] x 0 0


parser1 x "" flag1 flag2 = x
parser1 x (y:ys) flag1 flag2 =
        if(flag1==1) then
          if(y==' ') then
            (parser1 (x++[y]) (ys) 0 flag2)
          else
            (parser1 ((x++['.'])++[y]) (ys) 0 flag2)
        else if(flag2 ==1) then
          if(y==' ') then
            (parser1 (x++[y]) (ys) flag1 0)
          else
            (parser1 ((x++[','])++[y]) (ys) flag1 0)
        else
          if(y=='.') then parser1 x (ys) 1 flag2 else if (y==',') then parser1 x (ys) flag1 1 else if(y=='\'')||(y=='â€™') then parser1 x (ys) flag1 flag2 else parser1 (x++[y]) ys flag1 flag2

