decipher1 :: [Char] -> [Char]
decipher1 x = let rep_x = replace21 x
                  mapS = (mapping rep_x)
                  subD = (subDecipher1 rep_x mapS)
              in (randomSwap1 (subDecipher1 x mapS) subD temp (howFit (words(subD)) 0) 500)

