randomSwap1 :: [Char] -> [Char] -> [(Char, Char)] -> Int -> Int -> [Char]
randomSwap1 oldx x mapS fit 250 = (randomSing oldx x mapS fit 250)
randomSwap1 oldx x mapS fit 249 = (randomSing oldx x mapS fit 249)
randomSwap1 oldx x mapS fit n = let rand1 = fst (randData!!n)
                                    rand2 = rand1 + snd (randData!!n)
                                    rand3 = fst (randData!!(n-1))
                                    rand4 = rand3 + snd (randData!!(n-1))
                                    newX1  = (miniReplace x (snd (mapS!!rand1)) (snd (mapS!!rand2)))
                                    newX = (miniReplace newX1 (snd (mapS!!rand3)) (snd (mapS!!rand4)))
                                    howfit = (howFit (words (newX)) 0)
                                    modified1 = (swapMap mapS rand1 rand2)
                                    modified = (swapMap modified1 rand3 rand4)
                                in (if howfit>fit then (randomSwap1 oldx newX modified howfit (n-2)) else (randomSwap1 oldx x mapS fit (n-2)))

randomSing :: [Char] -> [Char] -> [(Char, Char)] -> Int -> Int -> [Char]
randomSing oldx x mapS fit 2 = subDecipher1 oldx mapS
randomSing oldx x mapS fit 1 = subDecipher1 oldx mapS
randomSing oldx x mapS fit n = let rand1 = fst (randData!!n)
                                   rand2 = rand1 + snd (randData!!n)
                                   newX = (miniReplace x (snd (mapS!!rand1)) (snd (mapS!!rand2)))
                                   howfit = (howFit (words (newX)) 0)
                                   modified = (swapMap mapS rand1 rand2)
                               in (if howfit>fit then (randomSing oldx newX modified howfit (n-1)) else (randomSing oldx x mapS fit (n-1)))

