parser :: [Char] -> [Char] -> Int -> Int -> Int -> [Char]
 
parser x "" flag1 flag2 n = x
parser x (y:ys) flag1 flag2 n=
  if(n<=500) then
        if(flag1 ==1 ) then
          if(y==' ') then
            (parser (x++[y]) (ys) 0 flag2 (n+1))
