mainCountS :: [Char] -> [(Char, Int)]
mainCountS x = freqS x [(' ',0),('m',0),('n',0),('b',0),('v',0),('c',0),('x',0),('z',0),('l',0),('k',0),('j',0),('h',0),('g',0),('f',0),('d',0),('s',0),('a',0),('p',0),('o',0),('i',0),('u',0),('y',0),('t',0),('r',0),('e',0),('w',0),('q',0)]

mainCountC :: [Char] -> [(Char, Int)]
mainCountC x = freqC x [('M',0),('N',0),('B',0),('V',0),('C',0),('X',0),('Z',0),('L',0),('K',0),('J',0),('H',0),('G',0),('F',0),('D',0),('S',0),('A',0),('P',0),('O',0),('I',0),('U',0),('Y',0),('T',0),('R',0),('E',0),('W',0),('Q',0)]

freqS :: [Char] -> [(Char, Int)] -> [(Char, Int)]
freqS "" y = y
freqS (x:xs) y = if ((x>='a' && x<='z')||x==' ') then freqS xs (addInfo x y) else freqS xs y

freqC :: [Char] -> [(Char, Int)] -> [(Char, Int)]
freqC "" y = y
freqC (x:xs) y = if (x<='Z' && x>='A') then freqC xs (addInfo x y) else freqC xs y

addInfo :: Char -> [(Char, Int)] -> [(Char, Int)]
addInfo c [] = [(c,1)]
addInfo c (y:ys) = if (fst y)==c then (c,(snd y)+1):ys else y:(addInfo c ys)

lt :: (Char,Int) -> (Char,Int) -> Bool
lt (c1,i1) (c2,i2) = if i1<i2 then True else False

gteq :: (Char,Int) -> (Char,Int) -> Bool
gteq (c1,i1) (c2,i2) = if i1>=i2 then True else False

qsort :: [(Char,Int)] -> [(Char,Int)]
qsort [] = []
qsort (p:xs) = (qsort lesser) ++ [p] ++ (qsort greater)
	where
		lesser  = filter (lt   p) xs
		greater = filter (gteq p) xs

sortByFreqS :: [Char] -> [(Char, Int)]
sortByFreqS x = qsort (mainCountS x)

sortByFreqC :: [Char] -> [(Char, Int)]
sortByFreqC x = qsort (mainCountC x)

