zipIt :: [(Char, Int)] -> [Char] -> [(Char, Char)]
zipIt [] [] = []
zipIt [] ys = []
zipIt xs [] = []
zipIt (x:xs) (y:ys) = (fst x, y) : (zipIt xs ys)

mappingS :: [Char] -> [(Char, Char)]
mappingS x = zipIt (sortByFreqS x) freqData

mappingC :: [Char] -> [(Char, Char)]
mappingC x = zipIt (sortByFreqC x) freqDataCap

replace :: Char -> [(Char, Char)] -> [(Char, Char)] -> Char
replace x [] [] = x
replace x [] (z:zs)  = if (x>'Z' || x<'A') then x else if (fst z)==x then (snd z) else replace x [] zs
replace x (y:ys) []  = if (x<='Z' && x>='A') then x else if (fst y)==x then (snd y) else replace x ys []
replace x (y:ys) (z:zs) = if ((x>'Z' || x<'A')&&(fst y)==x) then (snd y) else if ((x<='Z' && x>='A')&&(fst z)==x) then (snd z) else replace x ys zs

subDecipher :: [Char] -> [(Char, Char)] -> [(Char, Char)] -> [Char]
subDecipher [] y z = []
subDecipher (x:xs) y z = (replace x y z) : subDecipher xs y z


decipher :: [Char] -> [Char]
decipher x = deciphers (replace2 x ) x

deciphers :: [Char]-> [Char] -> [Char]
-- decipher x y = closest(words(subDecipher x (mappingS x) (mappingC x)))
deciphers x y = let mapS = (mappingS x)
                    mapC = (mappingC x)
                    subD = (subDecipher x mapS mapC)
                in (randomSwap y subD mapS mapC (howFit (words(subD)) 0) 600)

