miniReplace :: [Char] -> Char -> Char -> [Char]
miniReplace [] x y = []
miniReplace (a:as) x y = if a==x then y:(miniReplace as x y) else if a==y then x:(miniReplace as x y) else a:(miniReplace as x y)

swapMap :: [(Char, Char)] -> Int -> Int -> [(Char, Char)]
swapMap l x y = let save1 = snd (l !! x)
                    save2 = snd (l !! y)
                    (save3,save4:save5) = splitAt x l
                    save6 = save3++[(fst save4, save2)]++save5
                    (save7,save8:save9) = splitAt y save6
                in save7++[(fst save8,save1)]++save9



howFit :: [String] -> Int -> Int
howFit [] n = n
howFit (x:xs) n = if (binS (clean x) 0 ((length dictData)-1)) then (howFit xs (n+1)) else (howFit xs n)

