randomSwap :: [Char] -> [Char] -> [(Char, Char)] -> [(Char, Char)] -> Int -> Int -> [Char]
randomSwap full x mapS mapC fit 2 = (subDecipher full mapS mapC) 
randomSwap full x mapS mapC fit n = let rand1 = fst (randData!!n)
                                        rand2 = rand1 + snd (randData!!n)
                                        newX  = (miniReplace x (snd (mapS!!rand1)) (snd (mapS!!rand2)))
                                        noOfWords = length (words(newX))
                                        howfit = (howFit (words (newX)) 0)
                                        modified = (swapMap mapS rand1 rand2)
                                    in (if ((howfit>fit) && (((fromIntegral howfit) / (fromIntegral noOfWords)) > 0.9)) then (subDecipher full mapS mapC) else if howfit>fit then (randomSwap full newX modified mapC howfit (n-2)) else (randomSwap full x mapS mapC fit (n-2)))
