clean :: [Char] -> [Char]
clean [] = []
clean (x:xs) = if ((x>='a'&&x<='z')||(x>='A'&&x<='Z')) then (toLower x):(clean xs) else (clean xs)

binS :: String -> Int -> Int -> Bool
binS x start end
	| x==""                               = True
	| start>end                           = False
	| x==(dictData!!(quot (start+end) 2)) = True
	| x>(dictData!!(quot (start+end) 2))  = binS x ((quot (start+end) 2)+1) end
	| otherwise                           = binS x start ((quot (start+end) 2)-1)

