fitness :: [Char] -> Float
fitness [] = 0.0
fitness x = let total = fromIntegral(length (words (x)))
                howfit = fromIntegral(howFitBig (words(x)) 0)
            in (howfit/total)


howFitBig :: [String] -> Int -> Int
howFitBig [] n = n
howFitBig (x:xs) n = if (binSBig (clean x) 0 ((length dictBig)-1)) then (howFitBig xs (n+1)) else (howFitBig xs n)

binSBig :: String -> Int -> Int -> Bool
binSBig x start end
  | x==""                               = True
  | start>end                           = False
  | x==(dictBig!!(quot (start+end) 2)) = True
  | x>(dictBig!!(quot (start+end) 2))  = binSBig x ((quot (start+end) 2)+1) end
  | otherwise                           = binSBig x start ((quot (start+end) 2)-1)



prime :: Int -> String -> String
prime 1 x = decipher1 x
prime 2 x = decipher x